INSERT INTO WORDS VALUES ('back down', '(주장 등을) 굽히다, 패배를 인정하다', 'to take back a claim or a demand that you have made, or something that you have said, usually because somebody forces you to\n', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('back down from', '~에서 뒷걸음치다, ~에서 손을 때다\n(주장 등을) 철회하다, 포기하다', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('back in the day', '옛날에, 예전에', 'He founded the company back in 2001.\n', 0, '2016-10-16');
