INSERT INTO WORDS VALUES ('count on', '~을 믿다, ~을 확신하다', 'trust / depend on / rely on / count on / believe in\nto rely on somebody to do something; to expect something to happen and make plans in an appropriate way\n', 0, '2016-08-31');
