INSERT INTO WORDS VALUES ('delve', '(무엇을 찾으려고 가방 등을) 뒤지다', 'If you delve into something, you try to discover new information about it.\nIf you delve inside something such as a cupboard or a bag, you search inside it.\n', 0, '2016-08-31');
