INSERT INTO WORDS VALUES ('fond of beer', '맥주를 좋아하는', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('fondly', '[풘d뤼]\n애정을 듬뿍 담고\n허황되게', 'But she just smiled at him fondly.\n', 0, '2016-09-16');
INSERT INTO WORDS VALUES ('food', '[^푸우-d]\n식량, 음식, 식품, 먹이\n(특정한 유형의) 음식, ', 'Food is what people and animals eat.\nIf you are off your food, you do not want to eat, usually because you are ill.\nIf you give someone food for thought, you make them think carefully about something.\n', 0, '2016-11-03');
INSERT INTO WORDS VALUES ('food poisoning', '식중독', 'If you get food poisoning, you become ill because you have eaten food that has gone bad.\n', 0, '2016-11-03');
INSERT INTO WORDS VALUES ('fool', '[^푸울]\n바보, 얼간이, 어릿광대\n바보취급하다, 놀리다, 속이다', 'cheat / fool / deceive / betray / take in / trick / con\nIf you call someone a fool, you are indicating that you think they are not at all sensible and show a lack of good judgment.\nFool is used to describe an action or person that is not at all sensible and shows a lack of good judgment.\nIf someone fools you, they deceive or trick you.\nIf you say that a person is fooling with something or someone, you mean that the way they are behaving is likely to cause problems.\n', 0, '2016-11-09');
INSERT INTO WORDS VALUES ('fool about', '빈들빈들 지내다', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('fool around', '(할 일을 안하고) 노닥거리다, \n시간을 낭비하다, 가지고 놀다, 바보짓을 하다', 'to waste time or behave in a silly way\nto have a sexual relationship with another person''s partner or with somebody who is not your partner\nif two people fool around, they kiss and touch each other in a sexual way\n', 0, '2016-11-03');
INSERT INTO WORDS VALUES ('fool around with', '~와 장난치다', '', 0, '2016-08-31');
