INSERT INTO WORDS VALUES ('good reception', '잘 들리는 상태', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('good teeth', '고른 치열', 'She has a pretty smile because she has good teeth.\nHe has good teeth because he had dental braces.\n', 0, '2016-10-18');
