INSERT INTO WORDS VALUES ('have no choice but to', '~ 할 수밖에 없다\n~하는 것 외에는 방법이 없다', 'I have no choice but to wear glasses.\nWe had no choice but to go home.\nWhen her car broke down, she had no choice but to take a taxi.\nI have no choice but to pay.\n', 0, '2016-10-25');
