INSERT INTO WORDS VALUES ('loudspeaker', '[^라우ds삐커r]\n확성기, 스피커, ', 'A loudspeaker is a piece of equipment, for example part of a radio or hi-fi system, through which sound comes out.\n"Attention, please!" Miss Tingley, the school secretary, was speaking over the loudspeaker.\n', 0, '2016-10-25');
