INSERT INTO WORDS VALUES ('make sure', '반드시 (~하도록) 하다, \n(~을) 확실히 하다\n(~임을) 확인하다\n확실하게 하다', 'check that something is true or has been done\ndo something in order to be certain that something else happens\n', 0, '2016-10-16');
INSERT INTO WORDS VALUES ('make sure to', '반드시 ~하다\n~을 확실히 하다', 'A : Then start the meeting without me, but make sure to take notes.\nPlease make sure all belongings are with you.\n', 0, '2016-11-03');
