INSERT INTO WORDS VALUES ('unculture', '[안컨춰r]\n교양 없음, 무교육', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('uncultured', '[언컬춰rd]\n교양 없는, 세련되지 못한, \n개간/경작 되지 않은', 'If you describe someone as uncultured, you are critical of them because they do not seem to know much about art, literature, and other cultural topics.\n', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('undecided whether', '~의 여부를 결정하지 못한', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('undeniable', '[언더나이어붤]\n부인할 수없는, 명백한', 'If you say that something is undeniable, you mean that it is definitely true.\n', 0, '2016-08-31');
