INSERT INTO WORDS VALUES ('under a spell', '주문에 역다/걸다', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('under age', '미성년인 (~을 할) 법정 연령이 안되다', 'not yet an adult according to the law\n', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('under an alias', '가명으로', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('under an illusion', '착각 속에, 잘못 생각해서', '', 0, '2016-08-31');
