INSERT INTO WORDS VALUES ('up till then', '그 때 까지', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('up to', '(어떤 시점,정도,지점에) 이르기 까지', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('up to a point', '어느 정도(는)', 'to some extent; not completely\n', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('up to date', '현대식의, 최신 유행의\n(정보가) 최근의, 최신의\n현재까지', 'possessing the most recent information, ideas, etc. about something/somebody\nthe most recent, modern or fashionable\n', 0, '2016-10-24');
INSERT INTO WORDS VALUES ('up to recently', '최근까지', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('up to scratch', '만족스러운', 'at the good standard that is expected or needed\n', 0, '2016-08-31');
