INSERT INTO WORDS VALUES ('vase', '[^붸이s]\n꽃병, 항아리, (장식용) 단지', 'A vase is a jar, usually made of glass or pottery, used for holding cut flowers or as an ornament.\nThe vase shattered into pieces when he dropped it.\n', 0, '2016-10-18');
INSERT INTO WORDS VALUES ('vast', '[배st]\n광대한, 거대한, 막대한\n(범위, 크기, 양이) 어마어마 한, 방대한, 막대한', 'Something that is vast is extremely large.\n', 0, '2016-08-31');
