INSERT INTO WORDS VALUES ('with simplicity', '천진난만하게', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('with skill', '익숙하게', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('with some spirit', '다소 활기를 띠고', '', 0, '2016-08-31');
INSERT INTO WORDS VALUES ('with the best (of them)', '누구 못지않게', '', 0, '2016-08-31');
